var link=document.querySelector(".menu-btn"),submenu=document.querySelector(".menu");link.addEventListener("click",function(e){if(e.preventDefault(),submenu.classList)submenu.classList.toggle("active");else{var s=submenu.className.split(" "),n=s.indexOf(".active");n>=0?s.splice(n,1):s.push("active"),submenu.className=s.join(" ")}});